/** 获取方法参数列表*/
type ParamsType<T> = T extends (...args: infer P) => void ? P : never

/** 排除元组第一个元素*/
type OmitFirstFormatMessageParams<T extends any[] = []> =
  ((...args: T) => void) extends (first: any, ...args: infer P) => void ? P : never